package ch.bbw.zork;import java.util.HashMap;public class Room {		private String description;	private HashMap<String, Room> exits;	public Room(String description) {		this.description = description;		this.exits = new HashMap<String,Room>();	}	public void put(Room north, Room east, Room south, Room west) {		if (north != null) {			exits.put("north", north);		}		if (east != null) {			exits.put("east", east);		}		if (south != null) {			exits.put("south", south);		}		if (west != null) {			exits.put("west", west);		}	}	public String shortDescription() {		return description;	}	public String longDescription() {		String tempStr = "You are in " + description + ".\n";		tempStr += "Items in this room:\n";		tempStr += exitString();		return tempStr;	}	private String exitString() {		String returnString = "Exits:";		for (String key : exits.keySet()) {			returnString += " " + key;		}		return returnString;	}	public Room nextRoom(String direction) {		return exits.get(direction);	}}